#==CallBack Function==============
def myWorking(v):
 global Fs,N,tData,xData,xData1
 if v==0: xData1=np.round(128*xData)/128
 if v==1: xData1=np.round(64*xData)/64
 if v==2: xData1=np.round(32*xData)/32
 if v==3: xData1=np.round(16*xData)/16
 if v==4: xData1=np.round(8*xData)/8
 if v==5: xData1=np.round(4*xData)/4
 if v==6: xData1=0.8*np.round(2*xData)/2
 if v==7: xData1=0.8*np.round(xData) 
 mPlotWave2.setValue2D(tData,xData1)
 return 1

def myPlay(v):
 global Fs,N,tData,xData
 sa.play(1,Fs,xData1,0) 
 return 1

