mPlotWave2=dr.DRPlotX(win,20*wRate,355*hRate,1000*wRate,280*hRate,'','#002f5e','#000000','#ffffff','#00eeff','#ffff00',0,0.01,-1,1,0,0)
mBut=dr.DRGButton(win,930*wRate,50*hRate,90*wRate,36*hRate,'#003300','#00aa00','#ffffff',12,'Playing',12,101)
