Fs=44100;
dt=1.0/Fs;
N=40000;
tData=np.arange(N)*dt
xData=0.8*np.sin(2*np.pi*800*tData)
xData1=xData
mPlotWave1.setValue2D(tData,xData)
myWorking(0)

